var ss="";

var sheet=""; // be very careful ... it is the sheet name .. so it should match 
var url="";

function doPost(e){
var action = e.parameter.action;
var shId = e.parameter.sheetId;
var shName = e.parameter.sheetName;
ss = SpreadsheetApp.openById(shId)
sheet = ss.getSheetByName(shName);
if(action == 'addItem'){
 return addItem(e);
}

}





 function addItem(e){

var date =  new Date();

var id  =  "Item "+sheet.getLastRow(); // Item1

var label = e.parameter.label;
var data = e.parameter.data;

sheet.appendRow([date,id,label,data]);
   return ContentService.createTextOutput("Data inserted Successfully!").setMimeType(ContentService.MimeType.TEXT);
   return("Yes");
 }
